define(function(require, exports, module) {
    'use strict'

    // 这里之所以不用驼峰命名法，是与他们的容器保持一致
    module.exports = {
        detail: '\
            <section id="detail" class="detail">\
                <header>{{name}}</header>\
                <section id="map" class="map">\
                    <div id="map_canvas" class="map-canvas"></div>\
                </section>\
                <section class="contact-info">\
                    <ul class="c-list">\
                        <li class="address">\
                            <a class="c-item" href="{{mapLink}}">\
                                <div class="c-item-prefix icon-location"></div>\
                                <div class="c-item-content">\
                                    <address>{{addr}}</address>\
                                </div>\
                                <div class="c-item-suffix icon-right"></div>\
                            </a>\
                        </li>\
                        <li class="phone">\
                            <a href="tel:{{phoneNumber}}" class="c-item">\
                                <div class="c-item-prefix icon-phone"></div>\
                                <div class="c-item-content">\
                                    <p>{{phone}}</p>\
                                </div>\
                                <div class="c-item-suffix icon-right"></div>\
                            </a>\
                        </li>\
                        <li class="direction">\
                            <a class="c-item" href="{{mapLink}}">\
                                <div class="c-item-prefix icon-location"></div>\
                                <div class="c-item-content">\
                                    <p>查看路线信息</p>\
                                </div>\
                                <div class="c-item-suffix icon-right"></div>\
                            </a>\
                        </li>\
                    </ul>\
                </section>\
            </section>\
            {{#if isMovie}}\
            <section class="cinema">\
                <header>\
                    <!-- TODO 修改icon 和 链接地址 -->\
                    <a href="iMeituan://www.meituan.com/deal?did={{slug}}">\
                        <div class="c-item-content">\
                            <span class="icon-list"></span>影院排片表\
                        </div>\
                        <div class="c-item-suffix icon-right"></div>\
                    </a>\
                </header>\
            </section>\
            {{/if}}\
        '
        , all_deals: '\
            <section id="all_deals" class="all-deals">\
                <header><span class="icon-list"></span>本店团购</header>\
                {{#c-list data}}\
                  <a class="c-item" href="iMeituan://www.meituan.com/deal?did={{slug}}">\
                      <div class="c-item-content">\
                        <p>{{title}}</p>\
                      </div>\
                      <div class="c-item-suffix icon-right"></div>\
                  </a>\
                {{/c-list}}\
            </section>\
        '
        , nearby_deals: '\
            {{#if-greater-zero data}}\
            <section id="nearby_deals" class="nearby-deals">\
                <header><span class="icon-nearby"></span>附近团购</header>\
                {{#c-list data}}\
                    {{#if slug}}\
                    <a class="c-item" href="iMeituan://www.meituan.com/deal?did={{slug}}">\
                    {{else}}\
                    <a class="c-item more-deals">\
                    {{/if}}\
                        <div class="c-item-content">\
                            <header>\
                                <h6>{{mname}}</h6>\
                                {{#if distance}}\
                                    <p><{{distance}}</p>\
                                {{/if}}\
                            </header>\
                            <p>{{title}}</p>\
                        </div>\
                        <div class="c-item-suffix icon-right"></div>\
                    </a>\
                {{/c-list}}\
            </section>\
            {{/if-greater-zero}}\
        '
        , current_price: '\
            <section id="current_price" class="current-price">\
                <p id="price" class="price">&#165;{{price}}<del id="value" class="value">&#165;{{value}}</del></p>\
                <div class="buy-btn">\
                    立即抢购\
                </div>\
            </section>\
        '
        , movie_schedule: '\
            <section id="movie_schedule" class="movie-schedule">\
                {{#menu current}}{{/menu}}\
                {{#if errMsg}}\
                    <p class="errMsg">{{errMsg}}</p>\
                {{else}}\
                <ul id="container" class="container">\
                {{#each schedule}}\
                    <li class="movie">\
                        <!-- TODO iMeituan 链接 -->\
                        <a href="iMeituan://" class="name">\
                            <div class="c-item-content">{{name}}</div>\
                            <!-- TODO 这里的HTML结构可能需要调整 -->\
                            <div class="c-item-suffix">\
                                <span class="dur">{{duration}}分钟</span>\
                                <span class="icon-right"></span>\
                            </div>\
                        </a>\
                        <ul class="type-list">\
                        {{#each types}}\
                            <li class="movie-type">\
                                <h6>{{this}}</h6>\
                                {{#timelist ../this}}\
                                {{/timelist}}\
                            </li>\
                        {{/each}}\
                        </ul>\
                    </li>\
                {{/each}}\
                </ul>\
            </section>\
            {{/if}}\
        '
    }
})
